import React from 'react';

const Header = () => {
    const handleCalculatorScroll = () => {
        const calculatorSection = document.getElementById('calculator-section');
        if (calculatorSection) {
            // Smooth scroll with offset for header
            window.scrollTo({
                top: calculatorSection.offsetTop - 80,
                behavior: 'smooth'
            });

            // Visual feedback animation
            calculatorSection.classList.add('scroll-highlight');
            setTimeout(() => {
                calculatorSection.classList.remove('scroll-highlight');
            }, 1500);
        }
    };

    return (
        <>
            {/* TOP NAV BAR */}
            <header className="container" style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                padding: '1rem 0',
                position: 'sticky',
                top: 0,
                backgroundColor: 'white',
                zIndex: 1000
            }}>
                {/* Left: LLM Calc Logo & Name */}
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img
                        src="https://via.placeholder.com/40"
                        alt="LLM Calc Logo"
                        style={{ borderRadius: '4px', marginRight: '0.5rem' }}
                    />
                    <h1 style={{ fontSize: '1.25rem', fontWeight: 'bold', margin: 0 }}>LLM Calculator</h1>
                </div>

                {/* Middle: Nav Items */}
                <nav style={{ flex: '1 1 auto', marginLeft: '2rem' }}>
                    <ul style={{
                        listStyle: 'none',
                        display: 'flex',
                        gap: '1.5rem',
                        fontSize: '1rem'
                    }}>
                        <li style={{ cursor: 'pointer' }}>Providers</li>
                        <li style={{ cursor: 'pointer' }}>Benchmarks</li>
                        <li style={{ cursor: 'pointer' }}>Tutorial</li>
                        <li style={{ cursor: 'pointer' }}>FAQs</li>
                    </ul>
                </nav>
            </header>

            {/* HERO SECTION */}
            <section className="container" style={{
                textAlign: 'center',
                padding: '3rem 0'
            }}>
                <div style={{ marginBottom: '1rem' }}>
                    <span style={{
                        backgroundColor: '#ffe6db',
                        color: '#ff6200',
                        padding: '0.3rem 0.6rem',
                        borderRadius: '20px',
                        fontSize: '0.875rem',
                        fontWeight: '500'
                    }}>
                        Compare top LLMs & optimize your costs
                    </span>
                </div>

                <h2 style={{ fontSize: '3rem', fontWeight: 'bold', marginBottom: '1rem' }}>
                    Make LLM Costs Crystal Clear
                </h2>
                <p style={{ fontSize: '1.125rem', color: '#444', marginBottom: '2rem' }}>
                    Stop juggling tokens & unpredictable usage fees.<br />
                    Our unified dashboard calculates your LLM costs across multiple providers in seconds.
                </p>

                <button 
                    className="btn btn-primary scroll-trigger"
                    onClick={handleCalculatorScroll}
                    style={{ 
                        fontSize: '1rem',
                        padding: '14px 28px',
                        borderRadius: '8px',
                        transition: 'all 0.3s ease',
                        position: 'relative',
                        overflow: 'hidden'
                    }}
                >
                    Try the Calculator
                    <div className="btn-hover-effect"></div>
                </button>
                <div className="small-text" style={{ marginTop: '0.5rem' }}>
                    Instant cost estimates for GPT-4, Claude, Cohere, & more.
                </div>
            </section>

            {/* ... rest of Header component remains the same ... */}
        </>
    );
};

export default Header;
