// src/components/FeaturesGrid.jsx
import React from 'react';

const featuresData = [
  {
    icon: 'âš¡',
    title: 'Native, Fast & Powerful',
    description: 'Not another 200MB Electron app. Natively integrated with your favorite apps.'
  },
  {
    icon: 'ðŸ‘¥',
    title: 'Custom AI Assistants',
    description: 'Create custom AI assistants to handle specialized tasks or roles in your workflow.'
  },
  {
    icon: 'ðŸ“„',
    title: 'Prompt Library',
    description: 'Leverage a robust library of prompts to get the most out of your AI interactions.'
  },
  {
    icon: 'âŒ˜',
    title: 'Highly Customizable',
    description: 'Effortlessly configure shortcuts for seamless integration into your workflow.'
  },
  {
    icon: 'ðŸ”’',
    title: 'Secure',
    description: 'Keys are stored securely, with automatic redaction of sensitive data.'
  },
  {
    icon: 'ðŸ›¡',
    title: 'Respect Your Privacy',
    description: 'No middle servers. Your prompts go directly to OpenAI for best-in-class privacy.'
  }
];

const FeaturesGrid = () => {
  return (
    <section className="container" style={{ padding: '3rem 0' }}>
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h2 style={{ fontSize: '2rem', fontWeight: 'bold' }}>The AI era is here</h2>
        <p style={{ fontSize: '1rem', color: '#444', maxWidth: '680px', margin: '1rem auto' }}>
          AI won't replace humans. But those who know how to use it effectively will be more productive than those who don't. Integrate AI into your workflow with BoltAI today.
        </p>
      </div>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
        gap: '1.5rem'
      }}>
        {featuresData.map((feat, idx) => (
          <div key={idx} style={{
            border: '1px solid #eee',
            borderRadius: '8px',
            padding: '1.5rem',
            backgroundColor: '#fff'
          }}>
            <div style={{ fontSize: '2rem', marginBottom: '0.75rem' }}>{feat.icon}</div>
            <h4 style={{ fontSize: '1.125rem', fontWeight: 'bold', marginBottom: '0.5rem' }}>
              {feat.title}
            </h4>
            <p style={{ color: '#555', fontSize: '0.95rem', lineHeight: '1.4' }}>
              {feat.description}
            </p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default FeaturesGrid;
