import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables);

export default function App() {
  const [results, setResults] = useState([]);
  const [tokens, setTokens] = useState({ input: 1000, output: 500 });
  const [customProviders, setCustomProviders] = useState(() => {
    const saved = localStorage.getItem('customProviders');
    return saved ? JSON.parse(saved) : [];
  });
  const [newProvider, setNewProvider] = useState({
    name: '',
    model: '',
    inputCost: 0,
    outputCost: 0
  });
  const [editingIndex, setEditingIndex] = useState(null);

    // Add this useEffect hook
    useEffect(() => {
      localStorage.setItem('customProviders', JSON.stringify(customProviders));
    }, [customProviders]);

    const editProvider = (index) => {
      setNewProvider({ ...customProviders[index] });
      setEditingIndex(index);
    };

  const cancelEdit = () => {
    setNewProvider({ name: '', model: '', inputCost: 0, outputCost: 0 });
    setEditingIndex(null);
  };

  const updateProvider = () => {
    if (editingIndex !== null) {
      const updatedProviders = [...customProviders];
      updatedProviders[editingIndex] = { ...newProvider };
      setCustomProviders(updatedProviders);
      setNewProvider({ name: '', model: '', inputCost: 0, outputCost: 0 });
      setEditingIndex(null);
    }
  };

  const addCustomProvider = () => {
    if (newProvider.name && newProvider.model) {
      if (editingIndex !== null) {
        updateProvider();
      } else {
        setCustomProviders([...customProviders, { ...newProvider }]);
        setNewProvider({ name: '', model: '', inputCost: 0, outputCost: 0 });
      }
    }
  };

  const calculateCost = async () => {
    const response = await fetch('http://localhost:8000/calculate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        input_tokens: tokens.input,
        output_tokens: tokens.output,
        custom_providers: customProviders
      })
    });
    setResults(await response.json());
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <h1>LLM Cost Calculator</h1>

      {/* Token inputs */}
      <div style={{ margin: '20px 0' }}>
        <div style={{ display: 'inline-block', marginRight: '10px' }}>
          <div style={{ marginBottom: '5px' }}>Input Tokens</div>
          <input 
            type="text" 
            value={tokens.input.toLocaleString()}
            onChange={e => setTokens({...tokens, input: parseInt(e.target.value.replace(/,/g, '')) || 0})}
            style={{ padding: '5px' }}
          />
        </div>
        <div style={{ display: 'inline-block', marginRight: '10px' }}>
          <div style={{ marginBottom: '5px' }}>Output Tokens</div>
          <input
            type="text"
            value={tokens.output.toLocaleString()}
            onChange={e => setTokens({...tokens, output: parseInt(e.target.value.replace(/,/g, '')) || 0})}
            style={{ padding: '5px' }}
          />
        </div>
        <button 
          onClick={calculateCost}
          style={{ padding: '5px 15px', backgroundColor: '#4CAF50', color: 'white', border: 'none' }}
        >
          Calculate
        </button>
      </div>

      {/* Custom provider form */}
      <div style={{ margin: '20px 0', padding: '20px', backgroundColor: '#f5f5f5', borderRadius: '5px' }}>
        <h3>{editingIndex !== null ? 'Edit Provider' : 'Add Custom Provider'}</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px', marginBottom: '10px' }}>
          <input
            type="text"
            value={newProvider.name}
            onChange={e => setNewProvider({...newProvider, name: e.target.value})}
            placeholder="Provider Name"
            style={{ padding: '5px' }}
          />
          <input
            type="text"
            value={newProvider.model}
            onChange={e => setNewProvider({...newProvider, model: e.target.value})}
            placeholder="Model Name"
            style={{ padding: '5px' }}
          />
          <input
            type="number"
            value={newProvider.inputCost}
            onChange={e => setNewProvider({...newProvider, inputCost: Math.max(0, Number(parseFloat(e.target.value).toFixed(2)))})}
            min="0"
            step="0.01"
            placeholder="Input Cost Per Million Tokens (USD)"
            style={{ padding: '5px' }}
          />
          <input
            type="number"
            value={newProvider.outputCost}
            onChange={e => setNewProvider({...newProvider, outputCost: Math.max(0, Number(parseFloat(e.target.value).toFixed(2)))})}
            min="0"
            step="0.01"
            placeholder="Output Cost Per Million Tokens (USD)"
            style={{ padding: '5px' }}
          />
        </div>
        <div style={{ display: 'flex', gap: '10px' }}>
          <button 
            onClick={addCustomProvider}
            style={{ padding: '5px 15px', backgroundColor: '#2196F3', color: 'white', border: 'none' }}
          >
            {editingIndex !== null ? 'Update Provider' : 'Add Provider'}
          </button>
          {editingIndex !== null && (
            <button 
              onClick={cancelEdit}
              style={{ padding: '5px 15px', backgroundColor: '#ff4444', color: 'white', border: 'none' }}
            >
              Cancel Edit
            </button>
          )}
        </div>
      </div>

      {/* Custom providers list */}
      {customProviders.length > 0 && (
        <div style={{ margin: '20px 0' }}>
          <h3>Custom Providers</h3>
          <div style={{ display: 'grid', gap: '10px' }}>
            {customProviders.map((provider, index) => (
              <div key={index} style={{ 
                padding: '10px', 
                backgroundColor: '#f9f9f9', 
                borderRadius: '5px',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}>
                <div>
                  <strong>{provider.name} - {provider.model}</strong>
                  <div>Input Cost: ${provider.inputCost} / Output Cost: ${provider.outputCost}</div>
                </div>
                <button
                  onClick={() => editProvider(index)}
                  style={{ 
                    padding: '5px 10px', 
                    backgroundColor: '#FFA500', 
                    color: 'white', 
                    border: 'none',
                    borderRadius: '3px'
                  }}
                >
                  Edit
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Results section */}
      {results.length > 0 && (
        <div>
          <div style={{ height: '400px', margin: '20px 0' }}>
            <Bar
              data={{
                labels: results.map(r => `${r.provider} - ${r.model}`),
                datasets: [{
                  label: 'Total Cost ($)',
                  data: results.map(r => r.total_cost),
                  backgroundColor: [
                    '#ff6384', '#36a2eb', '#4bc0c0', '#ffcd56', '#ff9f40', 
                    '#9966ff', '#c9cbcf', '#4dc9f6', '#f67019', '#537bc4',
                    '#acc236', '#166a8f', '#00a950', '#58595b', '#8549ba'
                  ].slice(0, results.length)
                }]
              }}
              options={{
                animation: {
                  duration: 1000
                },
                plugins: {
                  tooltip: {
                    callbacks: {
                      label: (context) => {
                        const value = context.raw;
                        const minCost = Math.min(...results.map(r => r.total_cost));
                        const timesMoreExpensive = (value / minCost).toFixed(2);
                        return value === minCost
                          ? `$${value.toFixed(5)} (Cheapest)`
                          : `$${value.toFixed(5)} (${timesMoreExpensive}x more expensive)`;
                      }
                    }
                  }
                },
                scales: {
                  y: {
                    beginAtZero: true
                  }
                },
                plugins: {
                  annotation: {
                    annotations: {
                      labels: results.map((r, i) => ({
                        type: 'label',
                        content: r.total_cost === Math.min(...results.map(res => res.total_cost))
                          ? 'Cheapest'
                          : r.total_cost === Math.max(...results.map(res => res.total_cost))
                          ? 'Most Expensive'
                          : '',
                        xValue: i,
                        yValue: r.total_cost,
                        backgroundColor: 'rgba(255, 255, 255, 0.8)',
                        font: {
                          size: 12
                        },
                        padding: 4
                      }))
                    }
                  }
                }
              }}
            />
          </div>

          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ backgroundColor: '#f2f2f2' }}>
                <th style={{ padding: '10px', textAlign: 'left' }}>Provider</th>
                <th style={{ padding: '10px', textAlign: 'left' }}>Cost</th>
                <th style={{ padding: '10px', textAlign: 'left' }}>Input</th>
                <th style={{ padding: '10px', textAlign: 'left' }}>Output</th>
              </tr>
            </thead>
            <tbody>
              {results.map((result, i) => (
                <tr key={i} style={{ borderBottom: '1px solid #ddd' }}>
                  <td style={{ padding: '10px' }}>{result.provider}</td>
                  <td style={{ padding: '10px' }}>${result.total_cost.toFixed(5)}</td>
                  <td style={{ padding: '10px' }}>${result.input_cost.toFixed(5)}</td>
                  <td style={{ padding: '10px' }}>${result.output_cost.toFixed(5)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
